import { useNavigate } from 'react-router-dom';
import Web3 from 'web3';
import getABI from './ABI';
const web3 = new Web3(window.ethereum);
const contractAddress = "0xc700F3Ad3E85EA01F030e06A04541Aa342eaD4Af";
var myContract = new web3.eth.Contract(getABI(),contractAddress);
const accounts = await web3.eth.getAccounts();
export default  function Yeet() {
   const navigate = useNavigate();
   async function Destination() {
   console.log(await myContract.methods.isBorrower().call({
      from: accounts[1]
   }));
   var destination;
   if(
      await myContract.methods.isBorrower().call({
         from: accounts[0]
      })
   ) {destination = "Borrower";}
   else if(
      await myContract.methods.isLender().call({
         from: accounts[0]})
   ){destination= "Lender";}
   else {alert("You are not registered in the system!"); return}
   navigate("/"+destination);
   }
   return (
      <div className="buttonContainer">
      <button  onClick={Destination} className="greetButton">Login</button>
      </div>
   )
  
   
  
}
