import { myContract } from "./SmartContract";
import { web3 } from "./SmartContract";
const accounts = web3.eth.getAccounts();

async function closeAcc() {
    try{
        await myContract.methods.closeBorrower().call({
            from: accounts[0]
        });
    } catch(error) {
        console.log(error);
    }
}




export default function BorrowerButtons() {
    return(
        <>
        <div className="functionDiv"> 
            <div className="buttonContainer">
            <button  onClick={closeAcc} className="functionButton"><span className="closeSpan">Close Account</span></button>
            </div>
            <div className="buttonContainer">
            <button className="functionButton"><span className="loanSpan">Get a Loan</span></button>
            </div>
            <div className="buttonContainer">
            <button className="functionButton"><span className="repaySpan">Pay back</span></button>
            </div>
        </div>
        </>
    )
}
