import {Link} from "react-router-dom"
import Web3 from 'web3'
async function metamask()   {
    if(typeof window.ethereum !== "undefined") {
        try {
            await window.ethereum.request({method: "eth_requestAccounts"});
            const web3 = new Web3(window.ethereum);
            const accounts = await web3.eth.getAccounts();
            console.log("Account: ",accounts[0]);

        }catch (error) {
            console.log(error);
        }
    } else {
        console.log("No metamask installed");
    }

}
export default function Buttons() {
    return (
            <>
            <div className="buttonContainer">
            <button onClick={metamask}className="greetButton" >Connect <span className="metamaskSpan">Metamask</span></button>
            </div>
            <div className="buttonContainer">
            <button className="greetButton"><Link className="CustomLink" to="Borrower">Become a <span className="borrowSpan">borrower!</span></Link></button>
            <button className="greetButton">Become a <span className="lenderSpan">Lender!</span></button>
            </div>
            <div className="buttonContainer">
            <button className="greetButton">Login</button>
            </div>
            </>
    )
}
