import { myContract } from "./SmartContract"
import { web3 } from "./SmartContract";
var userData = await myContract.methods.borrowerArrJS().call();
var accounts = await web3.eth.getAccounts();
var userLoan = await myContract.methods.getLoanDetails().call();
//let myUser;
var userStatus;
var loanAmount = 0;
for(var i=0; i<userData.length; i++) {
    if(accounts[0] == userData[i][0]) {
        myUser = userData[i];
    }
}
if(myUser[2] == false) {
    userStatus = "Suspended";
} else {
    userStatus = "Active";
}
if(userLoan[0].length == 0) {
    loanAmount = 0;
} else {
    for(var i=0; i<userLoan[1].length; i++) {
        loanAmount += userLoan[1][i];
    }
}
export default function AccountData() {
    console.log(myUser);
    console.log(userLoan);
    return(
        
        <div className="dataDiv">
            <ul>
                <li>
                    Account address: {myUser[0]}
                </li>
                <li>
                    Credit score: {myUser[1]}
                </li>
                <li>
                    Borrowed Amount: {loanAmount}
                </li>
                <li>
                    Account Status: {userStatus}
                </li>
            </ul>
        </div>
    )
}
