import {NavLink} from "react-router-dom"
import Yeet from "./Yeet"
import {web3} from "./SmartContract";
import { myContract } from "./SmartContract";
//const web3 = new Web3(window.ethereum);


async function metamask()   {
    if(typeof window.ethereum !== "undefined") {
        try {
            await window.ethereum.request({method: "eth_requestAccounts"});
            const accounts = await web3.eth.getAccounts();
            console.log("Account: ",accounts[0]);

        }catch (error) {
            console.log(error);
        }
    } else {
        console.log("No metamask installed");
    }

}
//  const contractAddress = "0xc700F3Ad3E85EA01F030e06A04541Aa342eaD4Af";
//  var myContract = new web3.eth.Contract(getABI(),contractAddress);

const accounts = await web3.eth.getAccounts();

async function becomeBorrower() {
    console.log(accounts[0]);
   await myContract.methods.becomeBorrower().send({
        from: accounts[0],
        value: 300000000000
    })

}

async function becomeLender() {
   await myContract.methods.becomeLender().send({
        from: accounts[0],
        value: 300000000000
    })

}



export default function Buttons() {
    return (
            <>
            <div className="buttonContainer">
            <button onClick={metamask}className="greetButton" >Connect <span className="metamaskSpan">Metamask</span></button>
            </div>
            <div className="buttonContainer">
            <button onClick={becomeBorrower} className="greetButton"><NavLink className="CustomLink" to="Borrower">Become a <span className="borrowSpan">borrower!</span></NavLink></button>
            <button  onClick={becomeLender} className="greetButton"><NavLink className="CustomLink" to="Lender">Become a <span className="lenderSpan">Lender!</span></NavLink></button>
            </div>
            <Yeet/>
            </>
    )
}

