import {Link} from "react-router-dom"
import Web3 from 'web3'
import getABI from "./ABI";
const web3 = new Web3(window.ethereum);

async function metamask()   {
    if(typeof window.ethereum !== "undefined") {
        try {
            await window.ethereum.request({method: "eth_requestAccounts"});
            const accounts = await web3.eth.getAccounts();
            console.log("Account: ",accounts[0]);

        }catch (error) {
            console.log(error);
        }
    } else {
        console.log("No metamask installed");
    }

}
const ABI = getABI();
const smAddress = "0x8d4664FaACb5377B8c980400120f4d88b3F6F721";
const smartContract = new web3.eth.Contract(ABI,smAddress);
var accounts = await web3.eth.getAccounts();


async function becomeBorrower() {
   try{ await smartContract.methods.becomeBorrower().send({
        from: accounts[0],
        value: 300000000000
    })
} catch (error) {
    console.log(error);
}
}

async function becomeLender() {
    try{
        await smartContract.methods.becomeLender().send({
            from: accounts[0],
            value: 300000000000
        })
    } catch(error) {
        console.log(error);
    }
}

export default function Buttons() {
    return (
            <>
            <div className="buttonContainer">
            <button onClick={metamask}className="greetButton" >Connect <span className="metamaskSpan">Metamask</span></button>
            </div>
            <div className="buttonContainer">
            <button  onClick={becomeBorrower} className="greetButton"><Link className="CustomLink" to="Borrower">Become a <span className="borrowSpan">borrower!</span></Link></button>
            <button  onClick={becomeLender} className="greetButton">Become a <span className="lenderSpan">Lender!</span></button>
            </div>
            <div className="buttonContainer">
            <button className="greetButton">Login</button>
            </div>
            </>
    )
}
